const posts = $input.all();

console.log('=== RECENT BLOG POSTS ===');
console.log(`Found ${posts.length} recent posts:`);

const comparisonPosts = [];

posts.forEach((post, index) => {
  const data = post.json;
  console.log(`\n${index + 1}. ${data.title}`);
  console.log(`   - Slug: ${data.slug}`);
  console.log(`   - Status: ${data.status}`);
  console.log(`   - Published: ${data.published_at}`);
  console.log(`   - ID: ${data.id}`);

  // Check if this looks like a comparison post
  const title = data.title.toLowerCase();
  if (title.includes('comparison') || title.includes('compare') || title.includes('options') || title.includes('alberta traffic')) {
    comparisonPosts.push(data);
    console.log('   â­ POTENTIAL COMPARISON POST!');
  }
});

console.log(`\n=== COMPARISON POSTS FOUND: ${comparisonPosts.length} ===`);
comparisonPosts.forEach((post, index) => {
  console.log(`${index + 1}. "${post.title}" - slug: ${post.slug}`);
});

return {
  total_posts: posts.length,
  comparison_posts: comparisonPosts
};
